const moment = require('moment');

/**
  * Default config
  * @access private
  * @desc Default config which is overridden by implementing party's config.
*/
const defaultConfig = {
  name: 'Amido E2E Framework',
  featuresDir: '../../features',

  environment: 'local',
  defaultTimeout: 10000,

  createReport: true,
  reportsDir: '../../reports',

  takeScreenshotOnFailure: true,
  screenshotsDir: '../../reports/screenshots',

  fileSuffix: moment().format('DDMMYY-HHmmss'),

  localDriver: 'chrome',
  browserstack: {
    server: 'hub-cloud.browserstack.com',
    username: null,
    accessKey: null,

    capabilities: {
      browserName: 'Chrome',
      resolution: '1024x768',
    },
  },

  debug: false,
};

module.exports = defaultConfig;

'use strict';

const browser = require('../support/browser');

/**
  * Browser step definitions for use in feature files
  * @access public
  * @example Given I navigate to "http://www.google.com"
  * @example When I navigate to "http://www.google.com"
  * @example When I get the browser title
  * @example When I switch to the window named "window1"
  * @example When I switch to the frame named "frame1"
  * @example When I go back in the browser
  * @example When I go forward in the browser
  */
const browserStepDefs = function () {
  this.Given(/^I navigate to "([^"]*)"$/, function (url) {
    return browser.getUrl.call(this, url);
  });

  this.When(/^I navigate to "([^"]*)"$/, function (url) {
    return browser.getUrl.call(this, url);
  });

  this.When(/^I get the browser title"$/, function () {
    return browser.getTitle.call(this);
  });

  this.When(/^I switch to the window named "([^"]*)"$/, function (windowName) {
    return browser.switchToWindow.call(this, windowName);
  });

  this.When(/^I switch to the frame named "([^"]*)"$/, function (frameName) {
    return browser.switchToFrame.call(this, frameName);
  });

  this.When(/^I go back in the browser$/, function () {
    return browser.goBack.call(this);
  });

  this.When(/^I go forward in the browser$/, function () {
    return browser.goForward.call(this);
  });
};

module.exports = browserStepDefs;

const element = require('../support/element');

/**
  * Element step definitions for use in feature files
  * @access public
  * @example When I click the element with ID "my-id"
  * @example When I click the element with class "my-class"
  * @example When I click the element with tag "a"
  * @example When I click the element with name "my-name"
  * @example When I click the element with link text "My link"
  * @example When I click the element with partial link text "My li"
  * @example When I click the element with CSS selector ".my-class"
  * @example When I click the element with XPath "/html/body/article/p"
  * @example Then the element with ID "my-id" should exist
  * @example Then the element with class "my-class" should exist
  * @example Then the element with tag "a" should exist
  * @example Then the element with name "my-name" should exist
  * @example Then the element with link text "My link" should exist
  * @example Then the element with partial link text "My li" should exist
  * @example Then the element with CSS selector ".my-class" should exist
  * @example Then the element with XPath "/html/body/article/p" should exist
  */
const elementStepDefs = function () {
  // clicks

  this.When(/^I click the element with ID "([^"]*)"$/, function (id) {
    return element.findById.call(this, id).click();
  });

  this.When(/^I click the element with class "([^"]*)"$/, function (className) {
    return element.findByClassName.call(this, className).click();
  });

  this.When(/^I click the element with tag "([^"]*)"$/, function (tagName) {
    return element.findByTagName.call(this, tagName).click();
  });

  this.When(/^I click the element with name "([^"]*)"$/, function (name) {
    return element.findByName.call(this, name).click();
  });

  this.When(/^I click the element with link text "([^"]*)"$/, function (linkText) {
    return element.findByLinkText.call(this, linkText).click();
  });

  this.When(/^I click the element with partial link text "([^"]*)"$/, function (partialLinkText) {
    return element.findByPartialLinkText.call(this, partialLinkText).click();
  });

  this.When(/^I click the element with CSS selector "([^"]*)"$/, function (css) {
    return element.findByCss.call(this, css).click();
  });

  this.When(/^I click the element with XPath "([^"]*)"$/, function (xpath) {
    return element.findByXPath.call(this, xpath).click();
  });

  // existence

  this.Then(/^the element with ID "([^"]*)" should exist$/, function (id) {
    return element.findById.call(this, id);
  });

  this.Then(/^the element with class "([^"]*)" should exist$/, function (className) {
    return element.findByClassName.call(this, className);
  });

  this.Then(/^the element with tag "([^"]*)" should exist$/, function (tagName) {
    return element.findByTagName.call(this, tagName);
  });

  this.Then(/^the element with name "([^"]*)" should exist$/, function (name) {
    return element.findByName.call(this, name);
  });

  this.Then(/^the element with link text "([^"]*)" should exist$/, function (linkText) {
    return element.findByLinkText.call(this, linkText);
  });

  this.Then(/^the element with partial link text "([^"]*)" should exist$/, function (partialLinkText) {
    return element.findByPartialLinkText.call(this, partialLinkText);
  });

  this.Then(/^the element with CSS selector "([^"]*)" should exist$/, function (css) {
    return element.findByCss.call(this, css);
  });

  this.Then(/^the element with XPath "([^"]*)" should exist$/, function (xpath) {
    return element.findByXPath.call(this, xpath);
  });
};

module.exports = elementStepDefs;

const element = require('../support/element');
const form = require('../support/form');
const expect = require('chai').expect;

/**
  * Form step definitions for use in feature files
  * @access public
  * @example When I input the text "my text" into the element with ID "my-id"
  * @example When I input the text "my text" into the element with class "my-class"
  * @example When I input the text "my text" into the element with tag "input"
  * @example When I input the text "my text" into the element with name "my-name"
  * @example When I input the text "my text" into the element with CSS selector ".my-form-field"
  * @example When I input the text "my text" into the element with XPath "/html/body/article/input"
  * @example Then the element with ID "my-id" has the value "my text"
  * @example Then the element with class "my-class" has the value "my text"
  * @example Then the element with tag "input" has the value "my text"
  * @example Then the element with name "my-name" has the value "my text"
  * @example Then the element with CSS selector ".my-form-field" has the value "my text"
  * @example Then the element with XPath "/html/body/article/input" has the value "my text"
  */
const formStepDefs = function () {
  // input

  this.When(/^I input the text "([^"]*)" into the element with ID "([^"]*)"$/, function (text, id) {
    return form.inputText.call(this, text, element.findById.call(this, id).click());
  });

  this.When(/^I input the text "([^"]*)" into the element with class "([^"]*)"$/, function (text, className) {
    return form.inputText.call(this, text, element.findByClassName.call(this, className).click());
  });

  this.When(/^I input the text "([^"]*)" into the element with tag "([^"]*)"$/, function (text, tagName) {
    return form.inputText.call(this, text, element.findByTagName.call(this, tagName).click());
  });

  this.When(/^I input the text "([^"]*)" into the element with name "([^"]*)"$/, function (text, name) {
    return form.inputText.call(this, text, element.findByName.call(this, name).click());
  });

  this.When(/^I input the text "([^"]*)" into the element with CSS selector "([^"]*)"$/, function (text, css) {
    return form.inputText.call(this, text, element.findByCss.call(this, css).click());
  });

  this.When(/^I input the text "([^"]*)" into the element with XPath "([^"]*)"$/, function (text, xpath) {
    return form.inputText.call(this, text, element.findByXPath.call(this, xpath).click());
  });

  // value checks

  this.Then(/^the element with ID "([^"]*)" has the value "([^"]*)"$/, function (id, text) {
    return expect(element.findById.call(this, id).getAttribute('value')).to.equal(text);
  });

  this.Then(/^the element with class "([^"]*)" has the value "([^"]*)"$/, function (className, text) {
    return expect(element.findByClassName.call(this, className).getAttribute('value')).to.equal(text);
  });

  this.Then(/^the element with tag "([^"]*)" has the value "([^"]*)"$/, function (tagName, text) {
    return expect(element.findByTagName.call(this, tagName).getAttribute('value')).to.equal(text);
  });

  this.Then(/^the element with name "([^"]*)" has the value "([^"]*)"$/, function (name, text) {
    return expect(element.findByName.call(this, name).getAttribute('value')).to.equal(text);
  });

  this.Then(/^the element with CSS selector "([^"]*)" has the value "([^"]*)"$/, function (css, text) {
    return expect(element.findByCss.call(this, css).getAttribute('value')).to.equal(text);
  });

  this.Then(/^the element with XPath "([^"]*)" has the value "([^"]*)"$/, function (xpath, text) {
    return expect(element.findByXPath.call(this, xpath).getAttribute('value')).to.equal(text);
  });
};

module.exports = formStepDefs;

const element = require('../support/element');
const expect = require('chai').expect;

/**
  * Text step definitions for use in feature files
  * @access public
  * @example Then the element with ID "my-id" has the text "My text"
  * @example Then the element with class "my-class" has the text "My text"
  * @example Then the element with tag "a" has the text "My text"
  * @example Then the element with name "my-name" has the text "My text"
  * @example Then the element with link text "My link" has the text "My link"
  * @example Then the element with partial link text "My li" has the text "My link"
  * @example Then the element with CSS selector ".my-element" has the text "My text"
  * @example Then the element with XPath "/html/body/article/p" has the text "My text"
  */
const textStepDefs = function () {
  this.Then(/^the element with ID "([^"]*)" has the text "([^"]*)"$/, function (id, text) {
    return element.findById.call(this, id).getText()
      .then((elemText) => {
        expect(elemText).to.equal(text);
      });
  });

  this.Then(/^the element with class "([^"]*)" has the text "([^"]*)"$/, function (className, text) {
    return element.findByClassName.call(this, className).getText()
      .then((elemText) => {
        expect(elemText).to.equal(text);
      });
  });

  this.Then(/^the element with tag "([^"]*)" has the text "([^"]*)"$/, function (tagName, text) {
    return element.findByTagName.call(this, tagName).getText()
      .then((elemText) => {
        expect(elemText).to.equal(text);
      });
  });

  this.Then(/^the element with name "([^"]*)" has the text "([^"]*)"$/, function (name, text) {
    return element.findByName.call(this, name).getText()
      .then((elemText) => {
        expect(elemText).to.equal(text);
      });
  });

  this.Then(/^the element with link text "([^"]*)" has the text "([^"]*)"$/, function (linkText, text) {
    return element.findByLinkText.call(this, linkText).getText()
      .then((elemText) => {
        expect(elemText).to.equal(text);
      });
  });

  this.Then(/^the element with partial link text "([^"]*)" has the text "([^"]*)"$/, function (partialLinkText, text) {
    return element.findByPartialLinkText.call(this, partialLinkText).getText()
      .then((elemText) => {
        expect(elemText).to.equal(text);
      });
  });

  this.Then(/^the element with CSS selector "([^"]*)" has the text "([^"]*)"$/, function (css, text) {
    return element.findByCss.call(this, css).getText()
      .then((elemText) => {
        expect(elemText).to.equal(text);
      });
  });

  this.Then(/^the element with XPath "([^"]*)" has the text "([^"]*)"$/, function (xpath, text) {
    return element.findByXPath.call(this, xpath).getText()
      .then((elemText) => {
        expect(elemText).to.equal(text);
      });
  });
};

module.exports = textStepDefs;

'use strict';

const utility = require('../support/utility');

/**
  * Utility step definitions for use in feature files
  * @access public
  * @example When I sleep for "3000" milliseconds
  */
const utilityStepDefs = function () {
  this.When(/^I sleep for "([^"]*)" milliseconds$/, function (ms) {
    return utility.sleep.call(this, ms);
  });
};

module.exports = utilityStepDefs;

/**
  * Browser supporting functions available for use in spec declarations
  * @access public
  * @example const browserSupport = require('amido-frontend-e2e-framework').support.browser;
  * @example browserSupport.getUrl.call(this, url).then()...; // go to a URL
  * @example browserSupport.getTitle.call(this).then()...; // get the browser's title
  * @example browserSupport.switchToWindow.call(this, "windowName").then()...; // switch to a window
  * @example browserSupport.switchToFrame.call(this, "frameName").then()...; // switch to a frame
  * @example browserSupport.goBack.call(this).then()...; // go back in the browser
  * @example browserSupport.goForward.call(this).then()...; // go forward in the browser
  */
const browserSupport = {
  getUrl(url) {
    return this.driver.get(url);
  },

  getTitle() {
    return this.driver.getTitle();
  },

  switchToWindow(windowName) {
    return this.driver.switchTo().window(windowName);
  },

  switchToFrame(frameName) {
    return this.driver.switchTo().frame(frameName);
  },

  goBack() {
    return this.driver.back();
  },

  goForward() {
    return this.driver.forward();
  },
};

module.exports = browserSupport;

'use strict';

const config = require('../../lib/stores/config').get();

/**
  * Config bootstrapper
  * @access private
  */
const configSupport = function () {
  this.setDefaultTimeout(config.defaultTimeout || (60 * 1000));
};

module.exports = configSupport;

/**
  * Element supporting functions available for use in spec declarations
  * @access public
  * @example const elementSupport = require('amido-frontend-e2e-framework').support.element;
  * @example elementSupport.find.call(this, { id: 'my-id '}).then((elem) => {... // find element using keyed object
  * @example elementSupport.findById.call(this, 'my-id').then((elem) => {... // find element by ID
  * @example elementSupport.findByClassName.call(this, 'my-class').then((elem) => {... // find element/s by class
  * @example elementSupport.findByTagName.call(this, 'a').then((elem) => {... // find element/s by tag name
  * @example elementSupport.findByName.call(this, 'my-name').then((elem) => {... // find element/s by name
  * @example elementSupport.findByLinkText.call(this, 'Link text').then((elem) => {... // find element/s by link text
  * @example elementSupport.findByPartialLinkText.call(this, 'Link te').then((elem) => {... // find element/s by partial link text
  * @example elementSupport.findByCss.call(this, '.my-selector').then((elem) => {... // find element/s by CSS selector
  * @example elementSupport.findByXPath.call(this, '/html/body/article/p').then((elem) => {... // find element/s by XPath
  */
const elementSupport = {
  find(obj) {
    return this.driver.find(obj);
  },

  findById(id) {
    return this.driver.findElement({ id });
  },

  findByClassName(className) {
    return this.driver.findElement({ className });
  },

  findByTagName(tagName) {
    return this.driver.findElement({ tagName });
  },

  findByName(name) {
    return this.driver.findElement({ name });
  },

  findByLinkText(linkText) {
    return this.driver.findElement({ linkText });
  },

  findByPartialLinkText(partialLinkText) {
    return this.driver.findElement({ partialLinkText });
  },

  findByCss(css) {
    return this.driver.findElement({ css });
  },

  findByXPath(xpath) {
    return this.driver.findElement({ xpath });
  },
};

module.exports = elementSupport;

/**
  * Form supporting functions available for use in spec declarations
  * @access public
  * @example const formSupport = require('amido-frontend-e2e-framework').support.form;
  * @example formSupport.inputText.call(this, 'My text', elem); // input the text into an element
  * @example formSupport.submit.call(this, elem); // call the submit() method on the elem's closest form
  */
const formSupport = {
  inputText(text, elem) {
    return elem.sendKeys(text);
  },

  submit(elem) {
    return elem.submit();
  },
};

module.exports = formSupport;

/**
  * Text supporting functions available for use in spec declarations
  * @access public
  * @example const textSupport = require('amido-frontend-e2e-framework').support.text;
  * @example textSupport.getText.call(this, elem).then((text) => {... // get the innerText value of an element
  */
const textSupport = {
  getText(element) {
    return element.getText();
  },
};

module.exports = textSupport;

const seleniumWebdriver = require('selenium-webdriver');

/**
  * Utility supporting functions available for use in spec declarations
  * @access public
  * @example const utilitySupport = require('amido-frontend-e2e-framework').support.utility;
  * @example utilitySupport.wait.call(this, myConditionFunction, 3000); // wait 3s for myConditionFunction to pass - see http://www.seleniumhq.org/docs/03_webdriver.jsp
  * @example utilitySupport.waitForElementLocatedById.call(this, 'my-id', 3000); // wait 3s for element with ID to be located
  * @example utilitySupport.waitForElementLocatedByClassName.call(this, 'my-class', 3000); // wait 3s for element with class to be located
  * @example utilitySupport.waitForElementLocatedByTagName.call(this, 'a', 3000); // wait 3s for element with tag name to be located
  * @example utilitySupport.waitForElementLocatedByName.call(this, 'my-name', 3000); // wait 3s for element with name to be located
  * @example utilitySupport.waitForElementLocatedByLinkText.call(this, 'My link', 3000); // wait 3s for element with link text to be located
  * @example utilitySupport.waitForElementLocatedByPartialLinkText.call(this, 'My li', 3000); // wait 3s for element with partial link text to be located
  * @example utilitySupport.waitForElementLocatedByCss.call(this, '.my-class', 3000); // wait 3s for element with CSS selector to be located
  * @example utilitySupport.waitForElementLocatedByXPath.call(this, '/html/body/article/p', 3000); // wait 3s for element with XPath to be located
  * @example utilitySupport.sleep.call(this, 3000); // sleep the execution for 3s
  * @example utilitySupport.takeScreenshot.call(this).then((screenshotData) => { // take a screenshot (returns binary data which must be saved to disk)
  */
const utilitySupport = {
  wait(condition, ms) {
    return this.driver.wait(condition, ms);
  },

  waitForElementLocatedById(id, ms) {
    const condition = seleniumWebdriver.until.elementLocated({ id });

    return this.driver.wait(condition, ms);
  },

  waitForElementLocatedByClassName(className, ms) {
    const condition = seleniumWebdriver.until.elementLocated({ className });

    return this.driver.wait(condition, ms);
  },

  waitForElementLocatedByTagName(tagName, ms) {
    const condition = seleniumWebdriver.until.elementLocated({ tagName });

    return this.driver.wait(condition, ms);
  },

  waitForElementLocatedByName(name, ms) {
    const condition = seleniumWebdriver.until.elementLocated({ name });

    return this.driver.wait(condition, ms);
  },

  waitForElementLocatedByLinkText(linkText, ms) {
    const condition = seleniumWebdriver.until.elementLocated({ linkText });

    return this.driver.wait(condition, ms);
  },

  waitForElementLocatedByPartialLinkText(partialLinkText, ms) {
    const condition = seleniumWebdriver.until.elementLocated({ partialLinkText });

    return this.driver.wait(condition, ms);
  },

  waitForElementLocatedByCss(css, ms) {
    const condition = seleniumWebdriver.until.elementLocated({ css });

    return this.driver.wait(condition, ms);
  },

  waitForElementLocatedByXPath(xpath, ms) {
    const condition = seleniumWebdriver.until.elementLocated({ xpath });

    return this.driver.wait(condition, ms);
  },

  sleep(ms) {
    return this.driver.sleep(ms);
